import UserDict,string, os
from S2.directory import DATA_DIR
import NCAABB.prepare
DATA_DIR = DATA_DIR("ncaabb")


class look_back_dict(UserDict.UserDict):
    def __init__(self,d={}):
        UserDict.UserDict.__init__(self,d)
    def look_back_get(self,key,default=None):
        #print "look_back_dict.key:",key,type(key)
        if key in self.keys(): return self[key]
        elif 2000<key: return self.look_back_get(key-1,default)
        else: return default
# XXX use client input look back dict.
def conference_dict():
    d = {} # d[team][season]=conf
    for line in map(string.strip,open(os.path.join(DATA_DIR,"Join","_Raw","conf.tab")).readlines()[1:]):
        if not string.strip(line): continue
        #print 'line:',line
        abbr,conf,season = map(string.strip,string.split(line))
        if not d.has_key(abbr):
            d[abbr] = look_back_dict()
        d[abbr][int(season)] = conf
    return d
def team_dict():
    d = {} # d[alias][season]=team
    for line in map(string.strip,open(os.path.join(DATA_DIR,"Join","_Raw","team.tab")).readlines()[1:]):
        if not string.strip(line): continue
        #print line
        alias,team,season = map(string.strip,string.split(line))
        if not d.has_key(alias):
            d[alias] = look_back_dict()
        d[alias][int(season)] = team
    return d

TEAM_D = team_dict()
CONF_D = conference_dict()
def find_abbreviation(abbr,season,full):
    #print "find abbr gests",abbr,season,full,
    #if abbr == 'AFA':print 'fa gts',abbr,season,full
    if abbr == 'PSU' and 'Puget' in full:
        return "PUGET"
    if abbr == 'DETU': return 'DET'
    ret = ''
    if abbr == 'SMS' and 'Marys' in full: ret='SMKS'
    elif abbr in ['TTU','TNTEM'] and 'Texas' in full: ret='TXTCH'
    elif abbr in ['ORST','OREST']: ret='ORST'
    elif abbr == 'EWU' and 'West' in full: ret='EWU' # else this gets mapped to EWAS
    elif abbr == 'WEBB' and full=='Webber': ret='WEBB'
    elif abbr == 'GASOU' and 'Southwestern' in full: ret = 'GASW'
    elif abbr in ['GASO','GASOU'] and 'Georgia Southern' == full: ret = 'GASO'
    if not ret:
        if season > 2100: season = NCAABB.prepare.season_from_date(season)
        ret = TEAM_D.get(abbr,look_back_dict()).look_back_get(season,abbr)
    #if abbr == 'AFA':print "fa rets:",ret
    #print "and rets",ret
    return ret

def find_conference(abbr,season):
    return CONF_D.get(abbr,look_back_dict()).look_back_get(season,None)

